const BEEP = new Audio("data:audio/wav;base64,");

document.addEventListener("DOMContentLoaded", () => {
  let bars = [];
  let chart = document.getElementById('chart');

  for (let i = 0; i < 50; i++) {
    let value = document.createElement('div');
    value.style.width = `${i + 1}%`;
    value.value = i;
    bars[i] = value;
  }

  bars.sort( () => Math.random() - 0.5);

  bars.forEach((bar) => chart.appendChild(bar));

  document.getElementById('chart').addEventListener("click", event => {
    if (event.target.parentNode.id === 'chart') {
      event.target.classList.toggle('highlighted');
    }
  });

  document.getElementById('sort').addEventListener('click', event => {
    event.preventDefault();
    console.log('should sort');
    SortingAlgorithms.bubbleSort();
  });
});

class SortingAlgorithms {

  static get arr() {
    return [...document.getElementById('chart').querySelectorAll('div')];
  }

  static async bubbleSort(maxIndex = this.arr.length - 1) {

    let repeat = false;
    for (let index = 0; index < maxIndex; index++) {
      let a = this.arr[index];
      let b = this.arr[index+1];
      a.classList.add('highlighted')
      b.classList.add('highlighted')
      if (a.value > b.value) {
        await sleep(5);
        // BEEP.play();
        a.classList.add('swap-color');
        b.classList.add('swap-color');

        b.after(a);
        repeat = true;
      }
      await sleep(10);
      a.className = '';
      b.className = '';
    }
    if (repeat) this.bubbleSort(maxIndex - 1);
  }

  static async mergeSort() {

  }
}


function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}